import React,{useState} from "react";
import Button from "../UI/Button";
import Card from "../UI/Card";
import ErrorModal from "../UI/ErrorModal";
import classes from "./AddUser.module.css"

const AddUser = props =>{

    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [error,setError] = useState();

    const userNameChangeHandler = (event)=>{
        setEnteredUsername(event.target.value);
    }

    const ageChangeHandler = (event)=>{
        setEnteredAge(event.target.value);
    }

    const addUserHandler = (event)=>{
        event.preventDefault();

        if(enteredUsername.trim().length ===0 || enteredAge.trim().length === 0){
            setError({
                title:'Invalid input',
                message:'Please enter a valid name and age(non empty values)' 
            })
            return;
        } 
        if(+enteredAge<1){
            setError({
                title:'Invalid age',
                message:'Please enter a valid age (>0)' 
            })
            return;
        }
        props.onAddUser(enteredUsername,enteredAge);
        setEnteredUsername('');
        setEnteredAge('');
    } 

    return(
        <div>
            {error && <ErrorModal title= {error.title} message={error.message} onConfirm/>} 
            <Card className={classes.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor="username">Username</label>
                    <input onChange={userNameChangeHandler} value={enteredUsername} id="username" type="text"/>

                    <label htmlFor="age">Age (Years)</label>
                    <input onChange={ageChangeHandler} value={enteredAge} id="age" type="number"/>
                    <Button type="submit">Add User</Button>
                </form> 
            </Card>
        </div>
        
    )
}

export default AddUser;
